import React, { Component } from 'react'
import ViewCommentsInAllEvents from './ViewCommentsInAllEvents'
import TextField from '@material-ui/core/TextField';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import "./AllEvents.css"
import Grid from '@material-ui/core/Grid';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Avatar from '@material-ui/core/Avatar';
import blue from '@material-ui/core/colors/blue';
import CardHeader from '@material-ui/core/CardHeader';
import Typography from '@material-ui/core/Typography';
import MobileStepper from '@material-ui/core/MobileStepper';
import Paper from '@material-ui/core/Paper';
import KeyboardArrowLeft from '@material-ui/icons/KeyboardArrowLeft';
import KeyboardArrowRight from '@material-ui/icons/KeyboardArrowRight';
import PropTypes from 'prop-types';

const tutorialSteps = [
    {
        label: 'San Francisco – Oakland Bay Bridge, United States',
        imgPath:
            'https://images.unsplash.com/photo-1537944434965-cf4679d1a598?auto=format&fit=crop&w=400&h=250&q=60',
    },
    {
        label: 'Bird',
        imgPath:
            'https://images.unsplash.com/photo-1538032746644-0212e812a9e7?auto=format&fit=crop&w=400&h=250&q=60',
    },
    {
        label: 'Bali, Indonesia',
        imgPath:
            'https://images.unsplash.com/photo-1537996194471-e657df975ab4?auto=format&fit=crop&w=400&h=250&q=80',
    },
    {
        label: 'NeONBRAND Digital Marketing, Las Vegas, United States',
        imgPath:
            'https://images.unsplash.com/photo-1518732714860-b62714ce0c59?auto=format&fit=crop&w=400&h=250&q=60',
    },
    {
        label: 'Goč, Serbia',
        imgPath:
            'https://images.unsplash.com/photo-1512341689857-198e7e2f3ca8?auto=format&fit=crop&w=400&h=250&q=60',
    },
];

const styles = theme => ({
    container: {
        display: 'flex',
        flexWrap: 'wrap',
    },
    textField: {
        marginLeft: theme.spacing.unit,
        marginRight: theme.spacing.unit,
        width: 200,
    },
    card: {
        width: 600,
        marginTop: theme.spacing.unit,
        backgroundColor: '#e6f2ff',
        },
        media: {
            height: 0,
            paddingTop: '56.25%', // 16:9
        },
        expand: {
            transform: 'rotate(0deg)',
            marginLeft: 'auto',
            transition: theme.transitions.create('transform', {
                duration: theme.transitions.duration.shortest,
            }),
        },
        expandOpen: {
            transform: 'rotate(180deg)',
        },
        avatar: {
            backgroundColor: blue[500],
        },
         root: {
            maxWidth: 400,
            flexGrow: 1,
         },
         header: {
             display: 'flex',
             alignItems: 'center',
             height: 50,
             paddingLeft: theme.spacing.unit * 4,
             backgroundColor: theme.palette.background.default,
    },
    img: {
        height: 255,
        maxWidth: 400,
        overflow: 'hidden',
        display: 'block',
        width: '100%',
    },
});


 class AllEvents extends Component {
     constructor() {
         super();
         this.state = {
             searchNameEvent: '',
             searchStartPoint: '',
             searchTypeOfSport: '',
             events: [],
             activeStep: '0',
             maxSteps: 5
         }
     }


     handleNext = () => {
         this.setState(prevState => ({
             activeStep: prevState.activeStep + 1,
         }));
     };

     handleBack = () => {
         this.setState(prevState => ({
             activeStep: prevState.activeStep - 1,
         }));
     };

    componentDidMount(){
        fetch('/events/getAllEvents')
            .then(res => res.json())
            .then(events => this.setState({events}));
    }

     updateSearchNameEvent(event) {
         this.setState({searchNameEvent: event.target.value.substr(0,20)})
     }
     updateSearchStartPoint(event) {
         this.setState({searchStartPoint: event.target.value.substr(0,20)})
     }
     updateSearchTypeOfSport(event) {
         this.setState({searchTypeOfSport: event.target.value.substr(0,20)})
     }


     onSubmit (eventId) {
         if(localStorage.getItem('usertoken'))
         {
             localStorage.setItem('userEvent', eventId)
             this.props.history.push(`/addComment`)
         }
         else
         {
             alert("U must be log in")
         }
     }

    render(){
        const { classes } = this.props;
         let filteredEvents = this.state.events.filter(
                (event) => {
                     if(event.name_event.toLowerCase().indexOf(this.state.searchNameEvent.toLowerCase()) !== -1 && event.start_point.toLowerCase().indexOf(this.state.searchStartPoint.toLowerCase()) !== -1 && event.type_sport.toLowerCase().indexOf(this.state.searchTypeOfSport.toLowerCase()) !== -1)
                     {
                         return event;
                     }
                    return 0;
                }
            )
         return (

<div>
                     <Grid container direction="row" justify="center" alignItems="center" spacing={0}>
                         <Grid item>
                             <h1>Events</h1>
                         </Grid>
                     </Grid>
             <Grid container direction="row-reverse" justify="space-around" alignItems="center" spacing={8}>
               <Grid item>
                <TextField
                    name="Search name event"
                       placeholder="Search by name event"
                           variant={"outlined"}
                       type="text"
                       value={this.state.searchNameEvent}
                       onChange={this.updateSearchNameEvent.bind(this)} />
               </Grid>
                 <Grid item>
                <TextField
                    name="Search start"
                           variant={"outlined"}
                       placeholder="Search by where events will start"
                       type="text"
                       value={this.state.searchStartPoint}
                       onChange={this.updateSearchStartPoint.bind(this)} />
                 </Grid>
                 <Grid item>
                <TextField
                    name="Search type of sport"
                       placeholder="Search by type of sports"
                       type="text"
                           variant={"outlined"}
                       value={this.state.searchTypeOfSport}
                       onChange={this.updateSearchTypeOfSport.bind(this)} />
                 </Grid>
                 <div className={classes.root}>
                     <Paper square elevation={0} className={classes.header}>
                         <Typography>{tutorialSteps[this.state.activeStep].label}</Typography>
                     </Paper>
                     <img
                         className={classes.img}
                         src={tutorialSteps[this.state.activeStep].imgPath}
                         alt={tutorialSteps[this.state.activeStep].label}
                     />
                     <MobileStepper
                         steps={this.state.maxSteps}
                         position="static"
                         activeStep={this.state.activeStep}
                         className={classes.mobileStepper}
                         nextButton={
                             <Button size="small" onClick={this.handleNext} disabled={this.state.activeStep === this.state.maxSteps - 1}>
                                 Next
                                 {theme.direction === 'rtl' ? <KeyboardArrowLeft /> : <KeyboardArrowRight />}
                             </Button>
                         }
                         backButton={
                             <Button size="small" onClick={this.handleBack} disabled={activeStep === 0}>
                                 {theme.direction === 'rtl' ? <KeyboardArrowRight /> : <KeyboardArrowLeft />}
                                 Back
                             </Button>
                         }
                     />
                 </div>
             </Grid>
                <ul>
                    {filteredEvents.map(event =>
                        <li key={event.id_event}>
                            <Grid container direction="row" justify="center" alignItems="center" spacing={0}>
                                <Grid item>
                        <Card className={classes.card}>
                            <CardHeader
                                avatar={
                                    <Avatar aria-label="Recipe" className={classes.avatar}>
                                        {event.id_event}
                                    </Avatar>
                                }
                                title={event.type_sport}
                                subheader={"Start date: " + event.date}

                            />
                            <CardContent>
                                <Typography gutterBottom variant="h5" component="h2">
                                    {event.name_event}
                                </Typography>
                                <Typography component="p">
                                    Start point: {event.start_point} <br/>
                                    Advanced: {event.advanced} <br/>
                                    Repetition: {event.repetition}
                                </Typography>
                            </CardContent>
                            <CardActions>
                            <Button color="primary"
                                    onClick={() => {this.onSubmit(event.id_event)}}>
                                Comment
                            </Button>
                            </CardActions>
                        <ViewCommentsInAllEvents eventId={event.id_event}/>
                        </Card>
                                </Grid>
                            </Grid>
                        </li>
                    )}
                </ul>
</div>
        )

    }


}
AllEvents.propTypes = {
    classes: PropTypes.object.isRequired,
    theme: PropTypes.object.isRequired,
};
export default withStyles(styles)(AllEvents);
